01409 bool XFILEDBF::WriteRecord(XDWORD nrecord,XFILEDBF_RECORD* record)
01410 {
01411   if(!IsOpen()) return false;
01412   if(!header)   return false;
01413 
01414   int position = header->GetHeaderSize()+(header->GetSizeRecord()*nrecord);
01415 
01416   file->SetPosition(position);
01417 
01418   XBYTE delflag=record->IsDelete()?'*':' ';
01419 
01420   if(!file->Write(&delflag,1)) return false;
01421   if(!file->Write(record->GetData(0),header->GetSizeRecord()-1)) return false;
01422 
01423   return true;
01424 }
