01439 bool XFILEDBF::InsertRecord(XDWORD nrecord, XFILEDBF_RECORD* record)
01440 {
01441   if(!IsOpen()) return false;
01442   if(!header)   return false;
01443 
01444   int position = header->GetHeaderSize()+(header->GetSizeRecord()*nrecord);
01445 
01446   file->SetPosition(position);
01447 
01448   XBYTE delflag=record->IsDelete()?'*':' ';
01449 
01450   XBUFFER xbuffer;
01451 
01452   xbuffer.Add(delflag);
01453   xbuffer.Add(record->GetData(0), (header->GetSizeRecord()-1));
01454 
01455   if(!file->InsertBlock(xbuffer.Get(), xbuffer.GetSize()))   return false;
01456 
01457   header->SetNRecords(header->GetNRecords()+1);
01458   header->Update(file);
01459 
01460   return true;
01461 }
