01475 bool XFILEDBF::AddRecord(XFILEDBF_RECORD* record)
01476 {
01477   if(!IsOpen()) return false;
01478   if(!header)   return false;
01479 
01480   XDWORD size = header->GetSizeRecord()-1;
01481 
01482   XBYTE   specialend;
01483   XQWORD  position;
01484 
01485   file->SetPosition((XDWORD)XFILE_SEEKEND);
01486   file->GetPosition(position);
01487   file->SetPosition(position-1);
01488 
01489   if(file->Read(&specialend,1))
01490     {
01491       if(specialend == 0x1A) file->SetPosition(position-1);
01492     }
01493 
01494   XBYTE delflag=record->IsDelete()?'*':' ';
01495 
01496   if(!file->Write(&delflag,1)) return false;
01497   if(!file->Write(record->GetData(0),size)) return false;
01498 
01499   specialend = 0x1A;
01500   if(!file->Write(&specialend, 1)) return false;
01501 
01502   header->SetNRecords(header->GetNRecords()+1);
01503 
01504   header->Update(file);
01505 
01506   return true;
01507 }
