01521 bool XFILEDBF::DeleteRecord(XDWORD nrecord)
01522 {
01523   if(!IsOpen()) return false;
01524   if(!header)   return false;
01525 
01526   char*  data;
01527   int    size = header->GetSizeRecord()-1;
01528 
01529   data = new char[size+10];
01530   if(!data) return false;
01531 
01532   memset(data,0,size+10);
01533 
01534   int position = header->GetHeaderSize()+(header->GetSizeRecord()*nrecord);
01535 
01536   file->SetPosition(position);
01537 
01538   XBYTE delflag='*';
01539 
01540   if(!file->Write(&delflag,1)) return false;
01541   if(!file->Write((XBYTE*)data,size)) return false;
01542 
01543   delete[] data;
01544 
01545   return true;
01546 }
