01558 bool XFILEDBF::Pack()
01559 {
01560   if(!IsOpen()) return false;
01561   if(!header)   return false;
01562 
01563   XPATH xpathtmp;
01564 
01565   xpathtmp  = GetXPath()->Get();
01566   xpathtmp += __L(".tmp");
01567 
01568   XFILEDBF* dbftmp = new XFILEDBF();
01569   if(!dbftmp) return false;
01570 
01571   dbftmp->GetHeader()->CopyFrom(header);
01572   dbftmp->GetHeader()->SetNRecords(0);
01573 
01574   delete dbftmp->GetHeader()->GetHeaderData();
01575 
01576   if(!dbftmp->Create(xpathtmp))
01577     {
01578       delete dbftmp;
01579       return false;
01580     }
01581 
01582   for(XDWORD c=0; c< header->GetNRecords(); c++)
01583     {
01584       XFILEDBF_RECORD* record = ReadRecord(c);
01585       if(record)
01586         {
01587           if(!record->IsDelete())
01588             {
01589               dbftmp->AddRecord(record);
01590             }
01591 
01592           delete record;
01593         }
01594     }
01595 
01596   dbftmp->Close();
01597 
01598   delete dbftmp;
01599 
01600   Close();
01601 
01602 
01603   XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01604   if(!xfile) return false;
01605 
01606   bool status = true;
01607 
01608   if(xfile->Erase(GetXPath()->Get()))
01609     {
01610       if(!xfile->Rename(xpathtmp.Get(), GetXPath()->Get())) status = false;
01611 
01612     } else status = false;
01613 
01614   GEN_XFACTORY.Delete_File(xfile);
01615 
01616   if(status)
01617     {
01618       status = Open((*GetXPath()), GetPrimaryFile()->IsReadOnly());
01619       if(status)
01620         {
01621           delete GetHeader();
01622           CreateHeader();
01623           GetHeader()->Load(GetPrimaryFile());
01624         }
01625     }
01626 
01627   return status;
01628 }
