01643 bool XFILEDBF::Reduction(int maxrecords, int reductionpercent)
01644 {
01645   if(!IsOpen()) return false;
01646   if(!header)   return false;
01647 
01648   int nrecords  = header->GetNRecords();
01649   if(nrecords<maxrecords) return true;
01650 
01651   int nrectosave = (nrecords*reductionpercent)/100;
01652 
01653   for(int c=0;c<(nrecords-nrectosave);c++)
01654     {
01655       DeleteRecord(c);
01656     }
01657 
01658   if(!Pack()) return false;
01659 
01660   return true;
01661 }
