00394 bool XFILEDFU::ReadAll()
00395 {
00396   if(!xfilebase) return false;
00397 
00398   bool status = false;
00399 
00400   images.DeleteContents();
00401   images.DeleteAll();
00402 
00403   xfilebase->SetPosition(0);  
00404 
00405   //------------------------------------------
00406   // Read Prefix
00407 
00408   status = xfilebase->Read(prefix.signature, XFILEDFU_PREFIX_SIGNATUREPREXIF_MAX);
00409   if(status) status = xfilebase->Read(&prefix.version, 1);
00410   if(status) status = xfilebase->Read((XBYTE*)&prefix.imagesize, sizeof(XDWORD));
00411   if(status) status = xfilebase->Read(&prefix.ntargets, sizeof(XBYTE));
00412 
00413   if(status)
00414     {
00415       for(int c=0; c<prefix.ntargets; c++)
00416         {      
00417           XFILEDFU_IMAGE* image = Read_Image();
00418           if(image)images.Add(image);      
00419         }  
00420     }
00421 
00422   //------------------------------------------
00423   // Read Suffix
00424 
00425   status =xfilebase->Read((XBYTE*)&suffix.BCDdevice, sizeof(XWORD));
00426   if(status) status = xfilebase->Read((XBYTE*)&suffix.IDproduct, sizeof(XWORD));
00427   if(status) status = xfilebase->Read((XBYTE*)&suffix.IDvendor, sizeof(XWORD));
00428   if(status) status = xfilebase->Read((XBYTE*)&suffix.BCDDFU, sizeof(XWORD));
00429   if(status) status = xfilebase->Read((XBYTE*)&suffix.signature, XFILEDFU_PREFIX_SIGNATURESUFFIX_MAX);
00430   if(status) status = xfilebase->Read(&suffix.length, 1);
00431   if(status) status = xfilebase->Read((XBYTE*)&suffix.CRC, sizeof(XDWORD));
00432 
00433 
00434   //--------------------------------------------
00435   // Check CRC used in DFU.
00436   
00437   status = CheckCRC();
00438    
00439   return status;
00440 }
