00515 XFILEDFU_IMAGE* XFILEDFU::Read_Image()
00516 {
00517   XFILEDFU_IMAGE* image = new XFILEDFU_IMAGE();
00518   if(!image) return NULL;
00519 
00520   bool status = false;
00521 
00522   status = xfilebase->Read(image->signature, XFILEDFU_PREFIX_SIGNATUREIMAGE_MAX);
00523   if(status) xfilebase->Read(&image->alternatesetting, 1);
00524   if(status) xfilebase->Read((XBYTE*)&image->targetnameexist, sizeof(XDWORD));
00525   if(status) xfilebase->Read((XBYTE*)&image->targetname, XFILEDFU_IMAGEPREFIX_NAME_MAX);
00526   if(status) xfilebase->Read((XBYTE*)&image->targetsize, sizeof(XDWORD));
00527   if(status) xfilebase->Read((XBYTE*)&image->nelements, sizeof(XDWORD));
00528 
00529   for(XDWORD c=0; c<image->nelements; c++)
00530     {
00531       XFILEDFU_ELEMENT* element = Read_Element();
00532       if(element) image->elements.Add(element);       
00533     }
00534 
00535   if(!status) 
00536     { 
00537       delete image;
00538       return NULL;
00539     }
00540 
00541   return image;
00542 }
