00554 XFILEDFU_ELEMENT* XFILEDFU::Read_Element()
00555 {
00556   XFILEDFU_ELEMENT* element = new XFILEDFU_ELEMENT();
00557   if(!element) return NULL;
00558 
00559   bool status = false;
00560 
00561   status = xfilebase->Read((XBYTE*)&element->address, sizeof(XDWORD));
00562   if(status) xfilebase->Read((XBYTE*)&element->size, sizeof(XDWORD));
00563   if(status)
00564     {
00565       status = false;
00566       if(element->size)
00567         {
00568           status = element->data.Resize(element->size);
00569           if(status) status = xfilebase->Read(element->data.Get(), element->data.GetSize());            
00570         }
00571     }
00572 
00573   if(!status) 
00574     { 
00575       delete element;
00576       return NULL;
00577     }
00578 
00579   return element;
00580 }
