00592 bool XFILEDFU::CheckCRC()
00593 {
00594   XBUFFER buffer;
00595   bool    status;
00596 
00597   xfilebase->SetPosition(0);
00598   buffer.Resize((XDWORD)(xfilebase->GetSize() - sizeof(XDWORD)));
00599       
00600   status = xfilebase->Read(buffer.Get(), buffer.GetSize());
00601   if(status)
00602     {
00603       XDWORD CRC = 0xFFFFFFFF;
00604         for (XDWORD i = 0; i < buffer.GetSize(); i++) 
00605         {
00606               CRC = CRCtable[(CRC ^ buffer.Get()[i]) & 0xff] ^ (CRC >> 8);
00607             }
00608 
00609       if(CRC != suffix.CRC) status = false;                         
00610     }
00611 
00612   return status;
00613 }
