01525 bool XFILEINI::ConvertFromLines()
01526 {
01527   DeleteAllSections();
01528 
01529   XSTRING   section;
01530   XSTRING   key;  
01531   XSTRING   value;
01532   
01533   for(int c=0;c<GetNLines();c++)
01534     {
01535       XSTRING* line = GetLine(c);
01536       if(line)
01537         {          
01538           XFILEINIREMARK* remark =  new XFILEINIREMARK();   
01539           if(remark)
01540             {
01541               if(!line->HaveCharacters())
01542                 {
01543                   section.Empty();                  
01544                   key.Empty(); 
01545                   value.Empty();
01546                 }
01547                else
01548                 {
01549                   Line_GetRemark((*line), (*remark)); 
01550                   
01551                   XSTRING _section;
01552                   if(Line_IsSection((*line), _section))
01553                     {
01554                       section = _section;
01555 
01556                       if(!remark->GetTextRemark()->IsEmpty()) remark->SetType(XFILEINI_TYPEREMARK_IN_SECTION);
01557                       
01558                       CreateSection(section);
01559 
01560                       int index = 1;  
01561 
01562                       for(XDWORD c=0; c<remarks.GetSize(); c++)
01563                         {
01564                           XFILEINIREMARK* tmp_remark = remarks.Get(c);
01565                           if(tmp_remark)
01566                             {
01567                               if(tmp_remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01568                                 {
01569                                   if(tmp_remark->GetRelativeSection()->IsEmpty())  
01570                                     {
01571                                       tmp_remark->GetRelativeSection()->Set(section); 
01572                                       tmp_remark->GetRelativeKey()->Set(key);
01573                                       tmp_remark->SetRelativeYPos(index);
01574                                       index++;
01575                                     }
01576                                 } 
01577                             }                      
01578                         }
01579                     }
01580                    else
01581                     {            
01582                       XSTRING _key;
01583   
01584                       if(Line_IsKey((*line), _key, value))
01585                         {
01586                           key = _key;
01587 
01588                           if(!remark->GetTextRemark()->IsEmpty())
01589                             {
01590                               int index_found = value.Find(remark->GetTextRemark()->Get(), true, 0);
01591                               if(index_found != XSTRING_NOTFOUND) 
01592                                 {
01593                                   value.DeleteCharactersToEnd(index_found-1);   
01594 
01595                                   value.DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01596                                   value.DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01597                                 }
01598                             }
01599                           
01600                           if(!remark->GetTextRemark()->IsEmpty()) remark->SetType(XFILEINI_TYPEREMARK_IN_KEY);
01601 
01602                           int index = 1; 
01603                           for(XDWORD c=0; c<remarks.GetSize(); c++)
01604                             {
01605                               XFILEINIREMARK* tmp_remark = remarks.Get(c);
01606                               if(tmp_remark)
01607                                 {
01608                                   if(tmp_remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01609                                     {
01610                                       if(tmp_remark->GetRelativeSection()->IsEmpty())  
01611                                         {
01612                                           tmp_remark->GetRelativeSection()->Set(section); 
01613                                           tmp_remark->GetRelativeKey()->Set(key);
01614                                           tmp_remark->SetRelativeYPos(index);
01615                                           index++;
01616                                         }
01617                                     } 
01618                                 }                      
01619                             }
01620 
01621                           WriteValue(section, key, value);
01622                         }
01623                     }
01624                 }
01625 
01626               switch(remark->GetType())
01627                 {
01628                   case XFILEINI_TYPEREMARK_UNKNOWN     : delete remark;   
01629                                                          break; 
01630 
01631                   case XFILEINI_TYPEREMARK_ALL_LINE    : remarks.Add(remark);
01632                                                          break; 
01633 
01634                   case XFILEINI_TYPEREMARK_IN_SECTION  : remark->GetRelativeSection()->Set(section);
01635                                                          remark->GetRelativeKey()->Empty();
01636                                                          remarks.Add(remark);
01637                                                          break; 
01638 
01639                   case XFILEINI_TYPEREMARK_IN_KEY      : remark->GetRelativeSection()->Set(section);
01640                                                          remark->GetRelativeKey()->Set(key);
01641                                                          remarks.Add(remark);
01642                                                          break; 
01643                 }             
01644             }
01645         }
01646     }
01647 
01648   return true;
01649 }
01650 
01651 
01661 bool XFILEINI::ConvertToLines()
01662 {
01663   if(sections.IsEmpty()) return false;
01664 
01665   for(XDWORD c=0;c<sections.GetSize();c++)
01666     {
01667       XFILEINISECTION* section = (XFILEINISECTION*)sections.Get(c);
01668       if(section)
01669         { 
01670           XSTRING line(__L(" "));
01671           XSTRING text_remark;
01672           XSTRING key;
01673           XSTRING value;
01674 
01675           AddLine(line.Get());
01676          
01677           for(XDWORD d=0; d<remarks.GetSize(); d++)
01678             {
01679               XFILEINIREMARK* remark = remarks.Get(d);
01680               if(remark)
01681                 { 
01682                   line.Empty();
01683   
01684                   if(remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01685                     {
01686                       if((!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true))  && (remark->GetRelativeKey()->IsEmpty()))
01687                         {
01688                           for(XDWORD e=0; e<remark->GetXPos(); e++)
01689                             {
01690                               line.Add(__L(" "));
01691                             }
01692 
01693                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01694                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01695                           
01696                           line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());
01697                           AddLine(line.Get());
01698                         }    
01699                     }                     
01700                 }  
01701             }            
01702        
01703           line.Format(__L("[%s]"), section->GetName()->Get());
01704 
01705           for(XDWORD d=0; d<remarks.GetSize(); d++)
01706             {
01707               XFILEINIREMARK* remark = remarks.Get(d);
01708               if(remark)
01709                 { 
01710                   if(remark->GetType() == XFILEINI_TYPEREMARK_IN_SECTION)
01711                     {
01712                       if(!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true))
01713                         {
01714                           XDWORD inisizeline = line.GetSize();
01715 
01716                           for(XDWORD e=inisizeline; e<remark->GetXPos(); e++)
01717                             {
01718                               line.Add(__L(" "));
01719                             }
01720 
01721                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01722                           remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01723 
01724                           line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());
01725 
01726                           break;
01727                         }    
01728                     }                     
01729                 }  
01730             }           
01731           
01732           AddLine(line.Get());
01733 
01734           section->ResetSelectionKey();
01735 
01736           while(section->GetNextKey(key, value))
01737             {
01738               for(XDWORD d=0; d<remarks.GetSize(); d++)
01739                 {
01740                   XFILEINIREMARK* remark = remarks.Get(d);
01741                   if(remark)
01742                     { 
01743                       line.Empty();
01744 
01745                       if(remark->GetType() == XFILEINI_TYPEREMARK_ALL_LINE)
01746                         {
01747                           if((!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true)) && (!remark->GetRelativeKey()->Compare(key.Get(), true)))
01748                             {
01749                               for(XDWORD e=0; e<remark->GetXPos(); e++)
01750                                 {
01751                                   line.Add(__L(" "));
01752                                 }
01753 
01754                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01755                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01756 
01757                               line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());  
01758                               AddLine(line.Get());                     
01759                             }    
01760                         }                     
01761                     }  
01762                 }           
01763 
01764               line.Format(__L("%s=%s"), key.Get(), value.Get());
01765 
01766               for(XDWORD d=0; d<remarks.GetSize(); d++)
01767                 {
01768                   XFILEINIREMARK* remark = remarks.Get(d);
01769                   if(remark)
01770                     { 
01771                       if(remark->GetType() == XFILEINI_TYPEREMARK_IN_KEY)
01772                         {
01773                           if((!remark->GetRelativeSection()->Compare(section->GetName()->Get(), true)) && (!remark->GetRelativeKey()->Compare(key.Get(), true))) 
01774                             {
01775                               XDWORD inisizeline = line.GetSize();
01776 
01777                               for(XDWORD e=inisizeline; e<remark->GetXPos(); e++)
01778                                 {
01779                                   line.Add(__L(" "));
01780                                 }
01781 
01782                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_FROM_FIRST);    
01783                               remark->GetTextRemark()->DeleteCharacter(__C(' '),  XSTRINGCONTEXT_TO_END);
01784 
01785                               line.AddFormat(__L("; %s"), remark->GetTextRemark()->Get());
01786 
01787                               break;
01788                             }    
01789                         }                     
01790                     }  
01791                 }           
01792 
01793               AddLine(line.Get());
01794             }
01795         }
01796     }
01797 
01798   return true;
01799 }
