01801 
01813 bool XFILEINI::Serialization(bool read)
01814 {
01815   bool status;
01816 
01817   file->SetPosition(XFILE_SEEKINI);
01818 
01819   DeleteAllLines();
01820 
01821   if(read)
01822     {
01823       ReadAllFile();
01824       status = ConvertFromLines();
01825     }
01826    else
01827     {
01828       status = ConvertToLines();
01829       if(status)
01830         {
01831           WriteAllFile();
01832 
01833           XQWORD pos = 0;
01834           file->GetPosition(pos);
01835 
01836           if(pos) file->SetSize(pos);
01837         }
01838     }
01839 
01840   DeleteAllLines();
01841 
01842   return status;
01843 }
