01902 
01915 bool XFILEINI::Line_IsSection(XSTRING& line, XSTRING& section)
01916 {
01917   section.Empty();
01918 
01919   if(line.IsEmpty()) return false;
01920 
01921   line.Copy(__L("["),__L("]"),true,0,section);  
01922   if(section.IsEmpty()) return false;
01923  
01924   section.ToUpperCase();
01925   section.DeleteCharacter(__C(' '), XSTRINGCONTEXT_FROM_FIRST);
01926   section.DeleteCharacter(__C(' '), XSTRINGCONTEXT_TO_END);
01927 
01928   return true;
01929 }
01930 
01931 
01945 bool XFILEINI::Line_IsKey(XSTRING& line, XSTRING& key, XSTRING& value)
01946 {
01947   key.Empty();
01948   value.Empty();
01949 
01950   if(line.IsEmpty()) return false;
01951 
01952   if((line.Find(__L("["),true) != XSTRING_NOTFOUND) &&
01953      (line.Find(__L("]"),true) != XSTRING_NOTFOUND))  return false;
01954  
01955   int indexvalue = line.Find(__L("="),true);
01956   if(indexvalue == XSTRING_NOTFOUND) return false;
01957 
01958   line.Copy(0,indexvalue,key);
01959   line.Copy(indexvalue+1,line.GetSize(),value);
01960 
01961   if(key.IsEmpty()) return false;      
01962 
01963   //key.ToUpperCase();
01964   key.DeleteCharacter(__C(' '),XSTRINGCONTEXT_FROM_FIRST);
01965   key.DeleteCharacter(__C(' '),XSTRINGCONTEXT_TO_END);
01966 
01967   value.DeleteCharacter(__C(' '),XSTRINGCONTEXT_FROM_FIRST);
01968   value.DeleteCharacter(__C(' '),XSTRINGCONTEXT_TO_END);
01969 
01970   return true;
01971 }
01972 
01973 
01986 bool XFILEINI::Line_GetRemark(XSTRING& line, XFILEINIREMARK& remark)
01987 {
01988   if(line.IsEmpty()) return false;
01989 
01990   int index = line.Find(__L(";"), true);
01991   if(index == XSTRING_NOTFOUND) return false;
01992  
01993   remark.SetXPos(index);
01994   line.Copy(index+1, (*remark.GetTextRemark()));
01995 
01996   XSTRING first;
01997 
01998   if(index) line.Copy(0, index, first);
01999       
02000   if(!first.HaveCharacters())
02001     {      
02002       remark.SetType(XFILEINI_TYPEREMARK_ALL_LINE);     
02003       remark.SetRelativeYPos(-1);
02004     }
02005 
02006   first.DeleteCharacter(__C(' '), XSTRINGCONTEXT_TO_END);
02007   first.DeleteCharacter(__C(' '), XSTRINGCONTEXT_FROM_FIRST);
02008 
02009   line = first;
02010 
02011   return true;
02012 }
