00949 bool XFILERIFF::WriteListToFile(XFILERIFF_LIST* list, XBYTE* data, XDWORD datasize)  
00950 {
00951   if(!list) return false;
00952 
00953   XDWORD listdata[3]  = { 0, 0, 0 };
00954 //XQWORD position = 0;
00955 
00956   if(!xfilebase->SetPosition(list->GetPositionFileData())) return false;
00957 
00958   listdata[0] = list->GetType();
00959   listdata[1] = list->GetSize();
00960   
00961   if(list->IsTypeList())
00962     {      
00963       listdata[2] = list->GetTypeList();
00964       if(!xfilebase->Write((XBYTE*)listdata, (sizeof(XDWORD)*3))) return false;
00965     }
00966    else
00967     {
00968       listdata[1] = datasize;
00969       listdata[2] = 0;
00970 
00971       if(!xfilebase->Write((XBYTE*)listdata, (sizeof(XDWORD)*2))) return false;
00972 
00973       if(data && datasize)
00974         {
00975           if(!xfilebase->Write(data, datasize)) return false;
00976         }
00977     }
00978 
00979   return true;
00980 }
