01041 XDWORD XFILERIFF::AdjustSizeOfLists(XFILERIFF_LIST_NODE* node)
01042 {
01043   XFILERIFF_LIST* list        = NULL;
01044   XDWORD          currentsize = 0;
01045 
01046   list = node->GetData();  
01047   if(!list)  return 0;
01048 
01049   if(!list->IsTypeList()) return list->GetSize();
01050 
01051   for(int c=0; c<node->GetNChildren(); c++)
01052     {
01053       XFILERIFF_LIST_NODE* subnode = node->GetChild(c);
01054       if(subnode)
01055         {
01056           XFILERIFF_LIST* sublist = subnode->GetData();
01057           if(sublist)
01058             {
01059               if(sublist->IsTypeList())
01060                 {
01061                   currentsize += (AdjustSizeOfLists(subnode) + (sizeof(XDWORD) * 3));
01062                 }
01063                else 
01064                 {
01065                   currentsize += (sublist->GetSize() + (sizeof(XDWORD) * 2)); 
01066                 }        
01067             }
01068         }
01069     }
01070 
01071   list->SetSize(currentsize + sizeof(XDWORD));
01072 
01073   if(!WriteListToFile(list)) return false; 
01074 
01075   return currentsize;
01076 }
