01091 bool XFILERIFF::ConvertDWORDtoString(XDWORD data, XSTRING& string)
01092 {
01093   XCHAR characters[4] = { 0, 0 ,0 ,0 };
01094 
01095   characters[0] = (XCHAR)((data >> 24) & 0x000000FF);
01096   characters[1] = (XCHAR)((data >> 16) & 0x000000FF);
01097   characters[2] = (XCHAR)((data >> 8)  & 0x000000FF);
01098   characters[3] = (XCHAR)((data)       & 0x000000FF);
01099 
01100   string.Format(__L("%C%C%C%C"),  characters[3], characters[2], characters[1], characters[0]);
01101 
01102   string.ToUpperCase();
01103 
01104   return true;
01105 }
