01122 XFILERIFF_LIST* XFILERIFF::GetList(bool search_chunk, XFILERIFF_LIST_NODE* listnode, XDWORD type, XDWORD fathertypelist)
01123 {
01124   if(!listnode) return NULL;
01125 
01126   XFILERIFF_LIST* listriff = listnode->GetData();
01127   if(listriff)
01128     {
01129       if(listriff->IsTypeList() != search_chunk)
01130         {         
01131           if(fathertypelist  && listnode->GetParent())
01132             {
01133               XFILERIFF_LIST_NODE* fatherlistnode = listnode->GetParent();
01134               if(fatherlistnode)
01135                 {
01136                   XFILERIFF_LIST* fatherlistriff = fatherlistnode->GetData();
01137                   if(fatherlistriff)
01138                     {
01139                       if(fatherlistriff->GetTypeList() == fathertypelist)
01140                         {
01141                           if(search_chunk)
01142                             {
01143                               if(listriff->GetType() == type) 
01144                                 {
01145                                   return listriff;
01146                                 }
01147                             }
01148                            else
01149                             {
01150                               if(listriff->GetTypeList() == type) 
01151                                 {
01152                                   return listriff;
01153                                 }
01154                             }           
01155                         }                        
01156                     }
01157                 }
01158             }
01159            else
01160             { 
01161               if(search_chunk)
01162                 {
01163                   if(listriff->GetType() == type) 
01164                     {
01165                       return listriff;
01166                     }
01167                 }
01168                else
01169                 {
01170                   if(listriff->GetTypeList() == type) 
01171                     {
01172                       return listriff;
01173                     }
01174                 }              
01175             }                     
01176         }
01177     }
01178 
01179   for(XDWORD c=0; c<listnode->GetChildren()->GetSize(); c++)
01180     {
01181       XFILERIFF_LIST_NODE* listsubnode = listnode->GetChildren()->Get(c);
01182       if(listsubnode) 
01183         {
01184           XFILERIFF_LIST* sublistriff = GetList(search_chunk, listsubnode, type, fathertypelist);
01185           if(sublistriff)  return sublistriff;  
01186         }
01187     }
01188 
01189   return NULL;
01190 }
