01252                 }
01253                else
01254                 {
01255                   if(listriff->GetTypeList() == type) 
01256                     {
01257                       return listnode;
01258                     }
01259                 }              
01260             }                     
01261         }
01262     }
01263 
01264   for(XDWORD c=0; c<listnode->GetChildren()->GetSize(); c++)
01265     {
01266       XFILERIFF_LIST_NODE* sublistnode = listnode->GetChildren()->Get(c);
01267       if(sublistnode) 
01268         {
01269           XFILERIFF_LIST_NODE* sub2listnode = GetListNode(search_chunk, sublistnode, type, fathertypelist);
01270           if(sub2listnode)  return sub2listnode;  
01271         }
01272     }
01273 
01274   return NULL;
01275 }
