00632 bool XFILETXT::IsBinaryFile()
00633 {  
00634   if(!IsOpen()) return false;
00635   if(!file)     return false;
00636 
00637   bool istextfile = false;
00638   
00639   #define MAX_BUFFER_TEST 1024*5 
00640 
00641   XBUFFER buffer;
00642   XDWORD  size          = MAX_BUFFER_TEST;
00643   XDWORD  controlchars  = 0;
00644 
00645   buffer.Resize(MAX_BUFFER_TEST);
00646   file->Read(buffer.Get(), &size);
00647 
00648   if(size)
00649     {
00650       for(XDWORD c=0; c<size; c++)
00651         {
00652           if(((buffer.Get()[c] >= 0x00) && (buffer.Get()[c] < 0x09)) ||         
00653              ((buffer.Get()[c] >  0x0D) && (buffer.Get()[c] < 0x20)))
00654             {
00655               controlchars++;
00656             }
00657         }
00658 
00659       int percent = (controlchars*100/size);
00660       if(percent < 10) istextfile = true;
00661     }     
00662   
00663   return !istextfile;
00664 }
