00949 bool XFILETXT::ReadNLines(int nlines)
00950 {
00951   if(!file)           return false;
00952   if(!file->IsOpen()) return false;
00953 
00954   DeleteAllLines();
00955 
00956   XDWORD             sizeBOM              = 0;
00957   XFILETXTFORMATCHAR formatchar           = GetFormatCharFromFile(&sizeBOM);
00958   XDWORD             sizebytescharacter   = SizeOfCharacter(formatchar);
00959 
00960   if(this->formatchar==XFILETXTFORMATCHAR_UNKNOWN) this->formatchar = formatchar;
00961 
00962   file->SetPosition(sizeBOM);
00963 
00964   bool    endfile;
00965   XDWORD  br;
00966   XBUFFER dataline(false);
00967 
00968   XBYTE*  readbuffer = new XBYTE[XFILETXT_MAXBUFFER];
00969   if(!readbuffer) return false;
00970 
00971   memset(readbuffer, 0, XFILETXT_MAXBUFFER);
00972 
00973   do{ XDWORD bufferpos = 0;
00974 
00975       br      = XFILETXT_MAXBUFFER;
00976       endfile = !file->Read(readbuffer, &br);
00977       if(!br) break;
00978       
00979       do{ XFILETXTTYPELF  _typeLF   = XFILETXTTYPELF_UNKNOWN;
00980           XDWORD          sizeLF    = 0;
00981           XDWORD          sizeline  = 0;
00982           bool            endline   = GetSizeOfLine(formatchar, &readbuffer[bufferpos], _typeLF, sizeLF, sizeline, (br-bufferpos));
00983 
00984           if(typeLF == XFILETXTTYPELF_UNKNOWN && _typeLF != XFILETXTTYPELF_UNKNOWN) typeLF = _typeLF;
00985 
00986           if(endline)
00987             {
00988               dataline.Add(&readbuffer[bufferpos], sizebytescharacter*sizeline);
00989               AddLine(formatchar, dataline.Get(), (int)(dataline.GetSize()/sizebytescharacter));
00990               dataline.Delete();
00991 
00992               sizeline  *= sizebytescharacter;
00993               sizeline  += (sizeLF*sizebytescharacter);
00994               bufferpos += sizeline;
00995             }
00996            else
00997             {
00998               int lack = (br-bufferpos);
00999               dataline.Add(&readbuffer[bufferpos], lack);
01000               bufferpos += lack;
01001 
01002               if(endfile && (bufferpos == br))
01003                 {
01004                   AddLine(formatchar, dataline.Get(), (int)(lack/sizebytescharacter));
01005                   dataline.Delete();
01006                 }
01007             }
01008 
01009           if(nlines != XFILETXT_ALLLINES)
01010             {
01011               if(lines.GetSize() > (XDWORD)nlines)
01012                 {
01013                   break;
01014                 }
01015             }
01016           
01017         } while(bufferpos < br);
01018 
01019     } while(!endfile);
01020 
01021   delete [] readbuffer;
01022 
01023   return true;
01024 }
