01109       case XFILETXTFORMATCHAR_UNKNOWN     : break;
01110 
01111       case XFILETXTFORMATCHAR_ASCII       :
01112       case XFILETXTFORMATCHAR_UTF8        :
01113                                             string.ConvertFromUTF8((XBYTE*)line, sizeline);
01114                                             break;
01115 
01116       case XFILETXTFORMATCHAR_UTF16_BE    : { XCHAR* unibuffer = new XCHAR[sizeline];
01117                                               XWORD* wline     = (XWORD*)line;
01118 
01119                                               if(unibuffer)
01120                                                 {
01121                                                   for(XDWORD c=0; c<sizeline; c++)
01122                                                     {
01123                                                       wline[c]     = (wline[c]<<8) | (wline[c]>>8);
01124                                                       unibuffer[c] = wline[c];
01125                                                     }
01126 
01127                                                   string.Set(unibuffer,sizeline);
01128 
01129                                                   delete [] unibuffer;
01130                                                 }
01131                                              }
01132                                              break;
01133 
01134       case XFILETXTFORMATCHAR_UTF16_LE   :  {  XCHAR* unibuffer = new XCHAR[sizeline];
01135                                                XWORD*          wline     = (XWORD*)line;
01136                                                if(unibuffer)
01137                                                  {
01138                                                    for(XDWORD c=0; c<sizeline; c++)
01139                                                      {
01140                                                        unibuffer[c] = wline[c];
01141                                                      }
01142 
01143                                                    string.Set(unibuffer,sizeline);
01144 
01145                                                    delete [] unibuffer;
01146                                                  }
01147                                             }
01148                                             break;
01149     }
01150 
01151   return true;
01152 }
