01223 bool XFILETXT::AddLineAlready(XSTRING& line, XDWORD* resultsizeline, XDWORD* resultsizeLF)
01224 {
01225   XBYTE   LF[4]   = { 0,0,0,0 };
01226   XDWORD  sizeLF  = 0;
01227 
01228   CreateTypeLF(formatchar, typeLF, LF, sizeLF);
01229 
01230   file->SetPosition((XDWORD)XFILE_SEEKEND);
01231 
01232   bool status = true;
01233 
01234   XBYTE* buffer = new XBYTE[XFILETXT_MAXBUFFER];
01235   if(buffer)
01236     {
01237       int bw = 0;
01238       memset(buffer,0,XFILETXT_MAXBUFFER);
01239 
01240       switch(formatchar)
01241         {
01242           case XFILETXTFORMATCHAR_UNKNOWN   : break;
01243 
01244           case XFILETXTFORMATCHAR_ASCII     : { bw = line.GetSize();
01245 
01246                                                 XBUFFER charstr;
01247                                                 
01248                                                 line.ConvertToASCII(charstr); 
01249                                                 memcpy(buffer, (XBYTE*)charstr.Get(), bw);                                                
01250                                               }
01251                                               break;
01252 
01253           case XFILETXTFORMATCHAR_UTF8      : line.ConvertToUTF8(buffer,bw);
01254                                               break;
01255 
01256           case XFILETXTFORMATCHAR_UTF16_BE  : { XWORD* bufferw = (XWORD*)buffer;
01257 
01258                                                 bw = (line.GetSize()*2);
01259 
01260                                                 for(XDWORD d=0; d<line.GetSize(); d++)
01261                                                   {
01262                                                     bufferw[d] = (XWORD)line.Get()[d];
01263                                                     bufferw[d] = (bufferw[d]<<8) | (bufferw[d]>>8);
01264                                                   }
01265                                               }
01266                                               break;
01267 
01268           case XFILETXTFORMATCHAR_UTF16_LE  : { XWORD* bufferw = (XWORD*)buffer;
01269 
01270                                                 bw = (line.GetSize()*2);
01271 
01272                                                 for(XDWORD d=0; d<line.GetSize(); d++)
01273                                                   {
01274                                                     bufferw[d] = (XWORD)line.Get()[d];
01275                                                   }
01276                                               }
01277                                               break;
01278         }
01279 
01280       if(bw)
01281         {
01282           if(file->Write(buffer,bw))
01283             {
01284               if(file->Write(LF,sizeLF))
01285                 {
01286                   if(resultsizeline) (*resultsizeline) = bw;
01287                   if(resultsizeLF)   (*resultsizeLF)   = sizeLF;
01288 
01289                 }  else status = false;
01290 
01291             } else status = false;
01292         }
01293 
01294       delete [] buffer;
01295     }
01296 
01297   return status;
01298 }
