01390 
01403 bool XFILETXT::AddBufferLines(XFILETXTFORMATCHAR formatchar, XBUFFER& xbuffer)
01404 {
01405   if(xbuffer.IsEmpty()) return false;
01406 
01407   XBYTE* buffer = xbuffer.Get();
01408   if(!buffer) return false;
01409 
01410   int br    = xbuffer.GetSize();
01411   int nchar = 0;
01412 
01413   this->formatchar = formatchar;
01414 
01415   do{ XFILETXTTYPELF typeLF;
01416       XDWORD         sizeLF   = 0;
01417       XDWORD         sizeline = 0;
01418 
01419       bool endline = GetSizeOfLine(formatchar, &buffer[nchar], typeLF, sizeLF, sizeline, (br-nchar));
01420 
01421       if(!endline) sizeline = (br-nchar);
01422 
01423       if(this->typeLF == XFILETXTTYPELF_UNKNOWN) this->typeLF = typeLF;
01424 
01425       AddLine(formatchar, (XBYTE*) &buffer[nchar], sizeline);
01426 
01427       switch(formatchar)
01428         {
01429           case XFILETXTFORMATCHAR_UNKNOWN   :
01430           case XFILETXTFORMATCHAR_ASCII     :
01431           case XFILETXTFORMATCHAR_UTF8      : nchar+=(sizeline+(endline?sizeLF:0));
01432                                               break;
01433 
01434           case XFILETXTFORMATCHAR_UTF16_BE  :
01435           case XFILETXTFORMATCHAR_UTF16_LE  : nchar+=((sizeline*2)+(endline?(sizeLF*2):0));
01436                                               break;
01437         }
01438 
01439       } while(nchar<br);
01440 
01441   return true;
01442 }
