01444 
01461 bool XFILETXT::GetSizeOfLine(XFILETXTFORMATCHAR formatchar, XBYTE* buffer, XFILETXTTYPELF& typeLF, XDWORD& sizeLF, XDWORD& size, XDWORD maxsize)
01462 {
01463   bool    status = false;
01464   XDWORD  c      = 0;
01465 
01466   sizeLF = 0;
01467 
01468   typeLF = XFILETXTTYPELF_UNKNOWN;
01469 
01470   XWORD* bufw = (XWORD*) buffer;
01471   XBYTE* bufb = (XBYTE*) buffer;
01472 
01473   switch(formatchar)
01474     {
01475       case XFILETXTFORMATCHAR_UNKNOWN   :
01476       case XFILETXTFORMATCHAR_ASCII     :
01477       case XFILETXTFORMATCHAR_UTF8      : for(c=0; c<maxsize; c++)
01478                                             {
01479                                               if(bufb[c]==0x0D)
01480                                                 {
01481                                                   status = true;
01482                                                   sizeLF = 1;
01483                                                   typeLF = XFILETXTTYPELF_0D;
01484                                                   if(bufb[c+1]==0x0A)
01485                                                     {
01486                                                       typeLF = XFILETXTTYPELF_0D0A;
01487                                                       sizeLF++;
01488                                                     }
01489                                                   break;
01490                                                 }
01491 
01492                                               if(bufb[c]==0x0A)
01493                                                 {
01494                                                   status = true;
01495                                                   sizeLF = 1;
01496                                                   typeLF = XFILETXTTYPELF_0A;
01497                                                   if((c+1) < maxsize)
01498                                                     {
01499                                                       if(bufb[c+1]==0x0D)
01500                                                         {
01501                                                           typeLF = XFILETXTTYPELF_0A0D;
01502                                                           sizeLF++;
01503                                                         }
01504                                                     }
01505                                                   break;
01506                                                 }
01507                                             }
01508                                           break;
01509 
01510       case XFILETXTFORMATCHAR_UTF16_LE  : for(c=0;c<maxsize/2;c++)
01511                                             {
01512                                               if(bufw[c]==0x000D)
01513                                                 {
01514                                                   status = true;
01515                                                   sizeLF = 1;
01516                                                   typeLF = XFILETXTTYPELF_0D;
01517                                                   if(bufw[c+1]==0x000A)
01518                                                     {
01519                                                       typeLF = XFILETXTTYPELF_0D0A;
01520                                                       sizeLF++;
01521                                                     }
01522                                                   break;
01523                                                 }
01524 
01525                                               if(bufw[c]==0x000A)
01526                                                 {
01527                                                   status = true;
01528                                                   sizeLF = 1;
01529                                                   typeLF = XFILETXTTYPELF_0A;
01530                                                   if(bufw[c+1]==0x000D)
01531                                                     {
01532                                                       typeLF = XFILETXTTYPELF_0A0D;
01533                                                       sizeLF++;
01534                                                     }
01535                                                   break;
01536                                                 }
01537                                             }
01538                                           break;
01539 
01540       case XFILETXTFORMATCHAR_UTF16_BE  : for(c=0; c<maxsize/2; c++)
01541                                             {
01542                                               if(bufw[c]==0x0D00)
01543                                                 {
01544                                                   status = true;
01545                                                   sizeLF = 1;
01546                                                   typeLF = XFILETXTTYPELF_0D;
01547                                                   if(bufw[c+1]==0x0A00)
01548                                                     {
01549                                                       typeLF = XFILETXTTYPELF_0D0A;
01550                                                       sizeLF++;
01551                                                     }
01552                                                   break;
01553                                                 }
01554 
01555                                               if(bufw[c]==0x0A00)
01556                                                 {
01557                                                   status = true;
01558                                                   sizeLF = 1;
01559                                                   typeLF = XFILETXTTYPELF_0A;
01560                                                   if(bufw[c+1]==0x0D00)
01561                                                     {
01562                                                       typeLF = XFILETXTTYPELF_0A0D;
01563                                                       sizeLF++;
01564                                                     }
01565                                                   break;
01566                                                 }
01567                                             }
01568                                           break;
01569     }
01570 
01571   if(status) size = c;
01572 
01573   return status;
01574 }
