01622 bool XFILETXT::CopyTo(XFILETXT* filetxt)
01623 {
01624   if(!filetxt) return false;
01625 
01626   filetxt->formatchar = formatchar;
01627   filetxt->typeLF     = typeLF;
01628   filetxt->LF         = LF;
01629 
01630   filetxt->lines.DeleteContents();
01631   filetxt->lines.DeleteAll();
01632 
01633   for(int c=0; c<GetNLines(); c++)
01634     {
01635       XSTRING* line  = new XSTRING();
01636       if(line)
01637         {
01638           (*line) = GetLine(c)->Get();
01639           filetxt->lines.Add(line);
01640         }
01641     }
01642 
01643   return true;
01644 }
