01244 bool XFILEXML::DecodeAllLines()
01245 {
01246   DeleteAllElements();
01247 
01248   XSTRING   string;
01249   XSTRING*  stringml;
01250   bool      quote      =  false;
01251   bool      cdata      =  false;
01252   int       c          =  0;
01253 
01254   string.Empty();
01255 
01256   while(c<GetNLines())
01257     {
01258       stringml = GetLine(c);
01259 
01260       for(int d=0;d<(int)stringml->GetSize();d++)
01261         {
01262           XCHAR letter[2] = { 0, 0 };
01263           letter[0] = (*stringml)[d];
01264 
01265           if((*stringml)[d]==__C('\"')) quote=!quote;
01266 
01267           if((*stringml)[d]==__C('<')&&(!quote))
01268             {
01269               if(string.Find(__L("[CDATA["),false)!=XSTRING_NOTFOUND) cdata=true;
01270 
01271               if(!string.IsEmpty() && string.HaveCharacters() && (!cdata))
01272                 {
01273                   DecodeLine(string,false);
01274                   string.Empty();
01275                 }
01276 
01277               quote = false;
01278               string += letter;
01279             }
01280            else
01281             {
01282               if(((*stringml)[d]==__C('>'))&&(!quote))
01283                 {
01284                   if(string.Find(__L("]]"),false)!=XSTRING_NOTFOUND) cdata=false;
01285 
01286                   string += letter;
01287                   if(!string.IsEmpty() && string.HaveCharacters() && (!cdata))
01288                     {
01289                       DecodeLine(string,true);
01290                       string.Empty();
01291                     }
01292                 }
01293                else string += letter;
01294             }
01295         }
01296 
01297       c++;
01298     }
01299 
01300   DeleteAllLines();
01301 
01302   return true;
01303 }
