01636 int XFILEXML::DecodeLine(XSTRING& string,bool iselement)
01637 {
01638   XFILEXMLELEMENTTYPE     elementtype  = XFILEXMLELEMENTTYPE_UNKNOWN;
01639   XFILEXMLELEMENTTYPELINE typeline     = XFILEXMLELEMENTTYPELINE_UNKNOWN;
01640 
01641   if(iselement)
01642     {
01643       XSTRING str = string;
01644 
01645       str.DeleteNoCharacters(XSTRINGCONTEXT_ALLSTRING);
01646 
01647       int iini = str.FindCharacter(__C('<'));
01648       int iend = str.FindCharacter(__C('>'),0,true);
01649 
01650       if(iini!=XSTRING_NOTFOUND) iini++;
01651       if(iend!=XSTRING_NOTFOUND) iend--;
01652 
01653       switch(str[iini])
01654         {
01655           case __C('?') : if(str[iend]==__C('?')) elementtype = XFILEXMLELEMENTTYPE_CONFIG;
01656                           break;
01657 
01658           case __C('/') : elementtype = XFILEXMLELEMENTTYPE_NORMAL;
01659                           typeline    = XFILEXMLELEMENTTYPELINE_END;
01660                           break;
01661 
01662           case __C('!') : if((str[iini+1]==__C('-')) && (str[iini+2]==__C('-')) && (str[iend]  ==__C('-')) && (str[iend-1]==__C('-')))
01663                             elementtype = XFILEXMLELEMENTTYPE_COMMENT;
01664                           else
01665                           if(str.Find(__L("[CDATA["),false)!=XSTRING_NOTFOUND)
01666                             elementtype = XFILEXMLELEMENTTYPE_CDATA;
01667                           else
01668                           {
01669                             if((str[iini+1]==__C('-')) && (str[iini+2]==__C('-')))
01670                               is_multicomment=true;
01671                           }
01672 
01673                           break;
01674 
01675                default  : elementtype  = XFILEXMLELEMENTTYPE_NORMAL;
01676                           typeline     = XFILEXMLELEMENTTYPELINE_START;
01677                           if(str[iend]==__C('/'))  typeline    = XFILEXMLELEMENTTYPELINE_STARTEND;
01678                           break;
01679         }
01680 
01681       switch(elementtype)
01682         {
01683           case XFILEXMLELEMENTTYPE_UNKNOWN    : break;
01684           case XFILEXMLELEMENTTYPE_CONFIG   : DecodeConfig(string);             break;
01685           case XFILEXMLELEMENTTYPE_COMMENT  : DecodeComent(string);             break;
01686           case XFILEXMLELEMENTTYPE_CDATA    : DecodeCDATA(string);              break;
01687           case XFILEXMLELEMENTTYPE_NORMAL   : DecodeElement(string,typeline);   break;
01688         }
01689     }
01690    else
01691     {
01692       if(actualelement)
01693         {
01694           actualelement->SetValue(string);
01695         }
01696     }
01697 
01698 
01699   return 0;
01700 }
