01703 
01715 bool XFILEXML::DecodeConfig(XSTRING& string)
01716 {
01717   XSTRING values;
01718 
01719   if(string.Copy(__L("<?xml"),__L("?>"),false,0,values)==XSTRING_NOTFOUND)  return false;
01720 
01721   XSTRING name;
01722   XSTRING value;
01723 
01724   int indexname = 0;
01725 
01726   while(1)
01727     {
01728       indexname = values.Copy(indexname, __L("="),false,name);
01729       if(indexname==XSTRING_NOTFOUND) break;
01730 
01731       name.DeleteNoCharacters(XSTRINGCONTEXT_ALLSTRING);
01732 
01733       indexname = values.Copy(__L("\""),__L("\""),false,indexname,value);
01734       if(indexname==XSTRING_NOTFOUND) break;
01735 
01736       XFILEXMLATTRIBUTE* attribute = new XFILEXMLATTRIBUTE(name,value);
01737       if(!attribute) break;
01738 
01739       cfgattributes.Add(attribute);
01740     }
01741 
01742   return true;
01743 }
