01757 bool XFILEXML::DecodeComent(XSTRING& string)
01758 {
01759   if(!actualelement) return false;
01760   //if(ignorecomments) return true;
01761 
01762   XSTRING value;
01763 
01764   if(string.Copy(__L("<!--"),__L("-->"),false,0,value)==XSTRING_NOTFOUND)  return false;
01765 
01766   XFILEXMLELEMENT* element = actualelement->AddElement();
01767   if(!element) return false;
01768 
01769   element->SetType(XFILEXMLELEMENTTYPE_COMMENT);
01770   element->SetValue(value);
01771 
01772   return true;
01773 }
