01804 
01817 bool XFILEXML::DecodeElement(XSTRING& string, XFILEXMLELEMENTTYPELINE typeline)
01818 {
01819   switch(typeline)
01820     {
01821       case XFILEXMLELEMENTTYPELINE_UNKNOWN    : return false;
01822 
01823       case XFILEXMLELEMENTTYPELINE_START    :
01824       case XFILEXMLELEMENTTYPELINE_STARTEND : { XSTRING values;
01825                                                 XSTRING name;
01826                                                 XSTRING value;
01827 
01828                                                 if(string.Copy(__L("<"),__L(" "),false,0,name)==XSTRING_NOTFOUND) return false;
01829 
01830                                                 //name.Set(string);
01831                                                 name.DeleteCharacter(__C('<'),XSTRINGCONTEXT_ALLSTRING);
01832                                                 name.DeleteCharacter(__C('>'),XSTRINGCONTEXT_ALLSTRING);
01833 
01834                                                 name.DeleteNoCharacters();
01835                                                 name.DeleteNoCharacters(__L("\20"),0, XSTRINGCONTEXT_FROM_FIRST);
01836                                                 name.DeleteNoCharacters(__L("\20"),0, XSTRINGCONTEXT_TO_END);
01837 
01838 
01839                                                 XFILEXMLELEMENT* element;
01840 
01841                                                 if(!root)
01842                                                   {
01843                                                     element = new XFILEXMLELEMENT();
01844                                                     if(!element) return false;
01845                                                     root          = element;
01846                                                     actualelement = root;
01847                                                   }
01848                                                  else element = actualelement->AddElement();
01849 
01850                                                 if(!element) return false;
01851 
01852                                                 element->SetType(XFILEXMLELEMENTTYPE_NORMAL);
01853                                                 element->SetName(name);
01854 
01855                                                 if(string.Copy(name.Get(), (const XCHAR*)NULL, false, 0, values)!=XSTRING_NOTFOUND)
01856                                                   {
01857                                                     int indexname = 0;
01858 
01859                                                     while(1)
01860                                                       {
01861                                                         indexname = values.Copy(indexname, __L("="), false, name);
01862                                                         if(indexname==XSTRING_NOTFOUND) break;
01863 
01864                                                         name.DeleteNoCharacters(XSTRINGCONTEXT_ALLSTRING);
01865 
01866                                                         indexname = values.Copy(__L("\""),__L("\""),false,indexname,value);
01867                                                         if(indexname==XSTRING_NOTFOUND) break;
01868 
01869                                                         element->AddAtribute(name,value);
01870 
01871                                                         name.Empty();
01872                                                         value.Empty();
01873 
01874                                                       }
01875                                                   }
01876 
01877                                                 if(typeline==XFILEXMLELEMENTTYPELINE_START) actualelement = element;
01878 
01879                                               }
01880                                               break;
01881 
01882       case XFILEXMLELEMENTTYPELINE_END      : if(actualelement->GetFather()) actualelement = actualelement->GetFather();
01883                                               break;
01884     }
01885 
01886   return true;
01887 }
