01903 bool XFILEXML::AddTabs(int level,int spacetabs,XSTRING& string)
01904 {
01905   if(spacetabs<=0) return false;
01906 
01907   for(int c=0;c<level;c++)
01908     {
01909       for(int d=0;d<spacetabs;d++)
01910         {
01911           string += __L(" ");
01912         }
01913     }
01914 
01915   return true;
01916 }
