01930 bool XFILEXML::EncodeConfig(XSTRING& string)
01931 {
01932   string.Empty();
01933 
01934   string += __L("<?xml ");
01935 
01936   for(int c=0;c<(int)cfgattributes.GetSize();c++)
01937     {
01938       XFILEXMLATTRIBUTE* attribute = (XFILEXMLATTRIBUTE*)cfgattributes.Get(c);
01939       if(!attribute) break;
01940 
01941       string+=attribute->GetName();
01942       string+=__L("=");
01943       string+=__L("\"");
01944       string+=attribute->GetValue();
01945       string+=__L("\" ");
01946     }
01947 
01948   string += __L("?>");
01949 
01950   return true;
01951 }
