01953 
01966 bool XFILEXML::EncodeElements(bool istabulatedline, XFILEXMLELEMENT* element)
01967 {
01968   if(!element) return false;
01969 
01970   XSTRING string;
01971   XSTRING value  = element->GetValue();
01972   int     c;
01973 
01974   string.Empty();
01975   if(istabulatedline)
01976     {
01977       encodelevel++;
01978       AddTabs(encodelevel,XFILEXML_SPACETABS,string);
01979     }
01980 
01981   switch(element->GetType())
01982     {
01983       case XFILEXMLELEMENTTYPE_UNKNOWN    : break;
01984 
01985       case XFILEXMLELEMENTTYPE_CONFIG   : break;
01986 
01987       case XFILEXMLELEMENTTYPE_COMMENT  : { string += __L("<!--");
01988                                             string += value;
01989                                             string += __L("-->");
01990                                             AddLine(string);
01991                                             //XTRACE_PRINTCOLOR(1,string.Get());
01992                                           }
01993                                           break;
01994 
01995       case XFILEXMLELEMENTTYPE_CDATA    : { string += __L("<![CDATA[");
01996                                             string += value;
01997                                             string += __L("]]>");
01998                                             AddLine(string);
01999                                             //XTRACE_PRINTCOLOR(1,string.Get());
02000                                           }
02001                                           break;
02002 
02003       case XFILEXMLELEMENTTYPE_NORMAL   : { bool isclose = false;
02004 
02005                                             string += __L("<");
02006                                             string += element->GetName();
02007                                             if(element->GetNAttributes()) string += __L(" ");
02008 
02009                                             for(c=0;c<(int)element->GetNAttributes();c++)
02010                                               {
02011                                                 XFILEXMLATTRIBUTE* attribute = (XFILEXMLATTRIBUTE*)element->GetAttribute(c);
02012                                                 if(!attribute) break;
02013 
02014                                                 string+=attribute->GetName();
02015                                                 string+=__L("=");
02016                                                 string+=__L("\"");
02017                                                 string+=attribute->GetValue();
02018                                                 string+=__L("\"");
02019                                                 if(c!=element->GetNAttributes()-1) string+=__L(" ");
02020                                               }
02021 
02022                                             if(!element->GetNElements() && value.IsEmpty())
02023                                               {
02024                                                 string +=__L("/>");
02025                                                 isclose = true;
02026                                               }
02027                                              else string +=__L(">");
02028 
02029                                             AddLine(string);
02030                                             //XTRACE_PRINTCOLOR(1,string.Get());
02031 
02032                                             if(!value.IsEmpty())
02033                                               {
02034                                                 string.Empty();
02035                                                 if(istabulatedline) AddTabs(encodelevel+1,XFILEXML_SPACETABS,string);
02036                                                 string+=value;
02037 
02038                                                 AddLine(string);
02039                                                 //XTRACE_PRINTCOLOR(1,string.Get());
02040                                               }
02041 
02042                                             for(c=0;c<(int)element->GetNElements();c++)
02043                                               {
02044                                                 XFILEXMLELEMENT* subelement = element->GetElement(c);
02045                                                 if(subelement)  EncodeElements(istabulatedline,subelement);
02046                                               }
02047 
02048                                             if(!isclose)
02049                                               {
02050                                                 string.Empty();
02051 
02052                                                 if(istabulatedline) AddTabs(encodelevel,XFILEXML_SPACETABS,string);
02053 
02054                                                 string += __L("</");
02055                                                 string += element->GetName();
02056                                                 string += __L(">");
02057 
02058                                                 AddLine(string);
02059                                                 //XTRACE_PRINTCOLOR(1,string.Get());
02060                                               }
02061                                           }
02062                                           break;
02063 
02064 
02065     }
02066 
02067   if(istabulatedline) encodelevel--;
02068 
02069   return true;
02070 }
