01304 bool XFILEUNZIP::DecompressFile(XSTRING& namesource,XPATH& xpathtarget,XCHAR* nametarget, XCHAR* password)
01305 {
01306   XPATH xpath(namesource);
01307 
01308   XFILECMPZIP* filecmp = GotoFile(xpath);
01309   if(!filecmp) return false;
01310 
01311   bool status = DecompressFile(filecmp,xpathtarget,nametarget,password);
01312 
