01333 bool XFILEUNZIP::DecompressFile(XFILECMPZIP* filecmp, XPATH& xpathtarget, XCHAR* nametarget, XCHAR* password)
01334 {
01335   bool status = false;
01336 
01337   if(!filecmp) return false;
01338 
01339   XPATH xpath;
01340 
01341   xpath = xpathtarget;
01342 
01343   if(!nametarget)
01344     {
01345       xpath += filecmp->GetName();
01346     }
01347    else
01348     {
01349       if(!nametarget[0])
01350         {
01351           xpath += filecmp->GetName();
01352 
01353         } else xpath += nametarget;
01354     }
01355 
01356   XSTRING _password(password);
01357 
01358   if(filecmp->Open(_password))
01359     {
01360       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01361       if(xfile)
01362         {
01363           if(xfile->Create(xpath))
01364             {
01365               XBYTE* buffer= new XBYTE[maxsizebuffer];
01366               if(buffer)
01367                 {
01368                   XQWORD totalbytesread = 0;
01369                   int    bytesread      = 0;
01370 
01371                   do {  XFILEZIP_XEVENT xevent(this, XFILEZIP_XEVENT_TYPE_UNZIP_EXTRACTFILE);
01372 
01373                         xevent.GetFileName()->Set(xpathunzip.Get());
01374                         xevent.SetFileCompress(filecmp);
01375 
01376                         bytesread = maxsizebuffer;
01377                         memset(buffer, 0, maxsizebuffer);
01378 
01379                         filecmp->Read(buffer, &bytesread);
01380                         if(bytesread)
01381                           {
01382                             status = xfile->Write(buffer, bytesread);
01383                             if(status)
01384                               {
01385                                 totalbytesread += bytesread;
01386 
01387                                 lasterror            = XFILEZIP_ERROR_NONE;
01388                                 lastoperationpercent = (float)(totalbytesread*100) / filecmp->UnCompressSize();
01389 
01390                                 xevent.SetOperationPercent(lastoperationpercent);
01391 
01392                               } else lasterror = XFILEZIP_ERROR_UNZIP_EXTRACTFILEWRITE;
01393 
01394                            } else lasterror = XFILEZIP_ERROR_UNZIP_EXTRACTFILEREAD;
01395 
01396 
01397                         xevent.SetError(lasterror);
01398 
01399                         PostEvent(&xevent);
01400 
01401                         if(!status) break;
01402 
01403                      } while(bytesread == (int)maxsizebuffer);
01404 
01405                   delete [] buffer;
01406 
01407                   if(filecmp->UnCompressSize() != xfile->GetSize()) status = false;
01408                 }
01409 
01410               xfile->Close();
01411             }
01412 
01413           delete xfile;
01414 
01415         }
01416 
01417       filecmp->Close();
01418     }
01419 
01420   return status;
01421 }
