01433 bool XFILEUNZIP::Close()
01434 {
01435   if(!filehdl) return false;
01436 
01437   bool status = (unzClose(filehdl) == UNZ_OK) ? true : false;
01438 
01439   lastoperationpercent  = status ? 100.0f : 0.0f;
01440   lasterror             = XFILEZIP_ERROR_NONE;
01441 
01442   XFILEZIP_XEVENT xevent(this, XFILEZIP_XEVENT_TYPE_UNZIP_CLOSE);
01443 
01444   xevent.GetFileName()->Set(xpathunzip.Get());
01445   xevent.SetFileCompress(NULL);
01446   xevent.SetError(lasterror);
01447   xevent.SetOperationPercent(lastoperationpercent);
01448 
01449   PostEvent(&xevent);
01450 
01451   filehdl = NULL;
01452 
01453   return true;
01454 }
