00628 bool XINTERPOLATOR::Update(XQWORD delta)
00629 {
00630   switch(type)
00631     {
00632       case XINTERPOLATOR_TYPE_LIMIT     : Interpolate(delta);
00633 
00634                                           if(mode == XINTERPOLATOR_LIMITMODE_INCREMENTAL)
00635                                             {
00636                                               if(target<=current) current=target;
00637                                             }
00638                                            else
00639                                             {
00640                                               if(mode == XINTERPOLATOR_LIMITMODE_DECREMENTAL)
00641                                               if(target>=current) current = target;
00642                                             }
00643 
00644                                           break;
00645 
00646       case XINTERPOLATOR_TYPE_RELATIVE  :
00647       case XINTERPOLATOR_TYPE_ABSOLUTE  : if(current > target) current = target;
00648                                           if(current < 0)      current = 0;
00649                                           break;
00650 
00651       case XINTERPOLATOR_TYPE_NONE      : break;
00652     }
00653 
00654   return true;
00655 }
