00669 bool XINTERPOLATOR::Interpolate(XQWORD delta)
00670 {
00671   switch(factortype)
00672     {
00673       case XINTERPOLATOR_FACTOR_RELATIVE  : return InterpolateRelative(delta);
00674       case XINTERPOLATOR_FACTOR_LINEAR    : return InterpolateLinear  (delta);
00675       case XINTERPOLATOR_FACTOR_NONE      :
00676                                 default   : break;
