00694 bool XINTERPOLATOR::InterpolateLinear(XQWORD delta)
00695 {
00696   float   fdelta    = (float)(delta/100.0f);
00697   int     itarget   = (int)(target);
00698   float   result    = (float)current;
00699   int     margin    = 0;
00700 
00701   if(result ==  target) return false;
00702 
00703   if(result < itarget)
00704     {
00705       if (delta!=0)
00706         {
00707           if (carry<0.0000001f)  carry=0.0f;
00708 
00709           float inc = (speed*fdelta) + carry;
00710           carry = inc - float((int)inc);
00711 
00712           result += (int)inc;
00713         }
00714 
00715       if(result >= itarget)
00716         {
00717           result  = (float)itarget;
00718           carry   = 0.0f;
00719         }
00720 
00721     }
00722    else
00723     {
00724       if(delta!=0)
00725         {
00726           if(carry<0.0000001f) carry=0.0f;
00727 
00728           float inc=(speed*fdelta)+carry;
00729           carry=inc-float((int)inc);
00730 
00731           result -= (int)inc;
00732         }
00733 
00734       if(result <= float(itarget))
00735         {
00736           result = (float)itarget;
00737           carry  = 0.0f;
00738         }
00739     }
00740 
00741   current = (XDWORD)result;
00742 
00743   return true;
00744 }
