00758 bool XINTERPOLATOR::InterpolateRelative(XQWORD delta)
00759 {
00760   float   fdelta    = (float) (delta/100.0f);
00761   int     itarget   = (int)   (target);
00762   float   result    = (float) (current);
00763   int     margin    = 0;
00764 
00765   if(result ==  target) return false;
00766 
00767   if(result < itarget)
00768     {
00769       if(delta!=0)
00770         {
00771           if(carry<0.0000001f) carry=0.0f;
00772 
00773           float dif = (float)(itarget - result);
00774           float inc = ((speed/100.0f)*fdelta*dif)+carry;
00775 
00776           carry   = inc - float((int)inc);
00777           result += (int)inc;
00778         }
00779 
00780       if(result > (itarget-margin))
00781         {
00782           result  = (float)itarget;
00783           carry   = 0.0f;
00784         }
00785     }
00786    else
00787     {
00788       if(delta!=0)
00789         {
00790           if (carry<0.0000001f) carry=0.0f;
00791 
00792           float dif = (float)(result-itarget);
00793           float inc = ((speed/100.0f)*fdelta*dif)+carry;
00794 
00795           carry   = inc - float((int)inc);
00796           result -= (int)inc;
00797         }
00798 
00799       if(result < float(itarget+margin))
00800         {
00801           result = (float)itarget;
00802           carry  = 0.0f;
00803         }
