00854 bool XINTERPOLATOR::Serialize(XBUFFER* buffer)
00855 {
00856   XDWORD buffer_type,    buffer_target, buffer_mode,   buffer_factortype,buffer_max,      buffer_min,buffer_current,buffer_multiplier;
00857   float  buffer_speed;
00858 
00859   buffer->SetPosition(0);
00860 
00861   buffer->Get((XDWORD&)mask);
00862   buffer->Get((XDWORD&)buffer_type);
00863   buffer->Get((XDWORD&)buffer_mode);
00864   buffer->Get((XDWORD&)buffer_current);
00865   buffer->Get((XDWORD&)buffer_target);
00866   buffer->Get((float&)buffer_speed);
00867   buffer->Get((XDWORD&)buffer_max);
00868   buffer->Get((XDWORD&)buffer_min);
00869   buffer->Get((XDWORD&)buffer_multiplier);
00870   buffer->Get((XDWORD&)buffer_factortype);
00871 
00872   if(mask & XINTERPOLATOR_MASK_SPEED)      speed       = buffer_speed;
00873   if(mask & XINTERPOLATOR_MASK_MAXIMUN)    max         = buffer_max;
00874   if(mask & XINTERPOLATOR_MASK_MINIMUN)    min         = buffer_min;
00875   if(mask & XINTERPOLATOR_MASK_MULTIPLIER) multiplier  = buffer_multiplier;
00876   if(mask & XINTERPOLATOR_MASK_TYPE)       type        = (XINTERPOLATOR_TYPE)buffer_type;
00877   if(mask & XINTERPOLATOR_MASK_FACTOR)     factortype  = (XINTERPOLATOR_FACTOR)buffer_factortype;
00878   if(mask & XINTERPOLATOR_MASK_TARGET)     SetTarget(buffer_target);
00879   if(mask & XINTERPOLATOR_MASK_CURRENT)    SetCurrent(buffer_current);
00880 
00881   Update(0);
00882 
00883   return true;
