00490 bool XLINUXPROCESSMANAGER::Application_IsRunning(XCHAR* applicationname, XDWORD* ID)
00491 {
00492   DIR*            dir;
00493   struct dirent*  ent;
00494   char*           endptr;
00495   char            buf[512];
00496 
00497   if(!(dir = opendir("/proc"))) return -1;
00498 
00499   while((ent = readdir(dir)) != NULL)
00500     {
00501       // if endptr is not a null character, the directory is not entirely numeric, so ignore it
00502       long lpid = strtol(ent->d_name, &endptr, 10);
00503       if(*endptr != '\0')
00504         {
00505           continue;
00506         }
00507 
00508       // try to open the cmdline file
00509       snprintf(buf, sizeof(buf), "/proc/%ld/status", lpid);
00510       FILE* fp = fopen(buf, "r");
00511       if(fp)
00512         {
00513           if(fgets(buf, sizeof(buf), fp) != NULL)
00514             {
00515               // check the first token in the file, the program name
00516               char* first = strtok(buf, " ");
00517 
00518               XPATH nameall;
00519 
00520               nameall = first;
00521               nameall.SetOnlyNamefile();
00522               nameall.DeleteCharacter(__C(' ') , XSTRINGCONTEXT_FROM_FIRST);
00523               nameall.DeleteCharacter(__C('\t'), XSTRINGCONTEXT_FROM_FIRST);
00524               nameall.DeleteCharacter(__C('\n'), XSTRINGCONTEXT_TO_END);
00525               nameall.DeleteCharacter(__C('\r'), XSTRINGCONTEXT_TO_END);
00526 
00527               if(!nameall.Compare(applicationname, false))
00528                 {
00529                   if(ID) (*ID) = (XDWORD)lpid;
00530 
00531                   fclose(fp);
00532                   closedir(dir);
00533 
00534                   return true;
00535 
00536                 }
00537             }
00538 
00539           fclose(fp);
00540         }
00541     }
00542 
00543   closedir(dir);
00544 
00545   return false;
00546 }
