00424 bool XLINUXSYSTEM::GetMemoryInfo(XDWORD& total,XDWORD& free)
00425 {
00426   struct sysinfo meminfo;
00427 
00428   if(sysinfo(&meminfo) == -1) return false;
00429 
00430   long long totalvirtualmem = meminfo.totalram;
00431   //Add other values in next statement to avoid int overflow on right hand side...
00432   //totalvirtualmem += meminfo.totalswap;
00433   totalvirtualmem *= meminfo.mem_unit;
00434 
00435   long long virtualmemused = meminfo.totalram - meminfo.freeram;
00436   //Add other values in next statement to avoid int overflow on right hand side...
00437   //virtualmemused += meminfo.totalswap - meminfo.freeswap;
00438   virtualmemused *= meminfo.mem_unit;
00439 
00440   total = (XDWORD)(totalvirtualmem                     / 1024);
00441   free  = (XDWORD)((totalvirtualmem - virtualmemused)  / 1024);
