00473 int XLINUXSYSTEM::GetCPUUsageTotal()
00474 {
00475   if(xmutexcheckCPUusage)
00476     {
00477       xmutexcheckCPUusage->Lock();
00478     }
00479 
00480   static unsigned int lasttotaluser, lasttotaluserlow, lasttotalsys, lasttotalidle;
00481   float               percent;
00482   int                 intpercent;     
00483   FILE*               file;
00484   unsigned long long  totaluser, totaluserlow, totalsys, totalidle, total;
00485      
00486   file = fopen("/proc/stat", "r");
00487   fscanf(file, "cpu %llu %llu %llu %llu", &totaluser, &totaluserlow, &totalsys, &totalidle);
00488   fclose(file);
00489      
00490   if(totaluser < lasttotaluser || totaluserlow < lasttotaluserlow || totalsys < lasttotalsys || totalidle < lasttotalidle)
00491     {
00492       // Overflow detection. Just skip this value.
00493       percent = -1.0;
00494     }
00495    else
00496     {
00497       total    = (totaluser - lasttotaluser) + (totaluserlow - lasttotaluserlow) + (totalsys - lasttotalsys);
00498       percent  = total * 100;
00499       total   += (totalidle - lasttotalidle);
00500       percent /= total;
00501     }
00502      
00503   lasttotaluser    = totaluser;
00504   lasttotaluserlow = totaluserlow;
00505   lasttotalsys     = totalsys;
00506   lasttotalidle    = totalidle;
00507      
00508   intpercent = percent;
00509 
00510   if(xmutexcheckCPUusage)
00511     {
00512       xmutexcheckCPUusage->UnLock();
00513     }
00514             
00515   return intpercent;
00516 }
