00530 int XLINUXSYSTEM::GetCPUUsageForProcessName(XCHAR* processname)
00531 {  
00532   XSTRING command;
00533   XSTRING result;
00534   XBUFFER charcmd;
00535   
00536   command.Format(__L("ps -C %s -o %%cpu"), processname);
00537 
00538   command.ConvertToASCII(charcmd);  
00539 
00540   // Open a pipe to the command
00541   FILE* pipe = popen((char*)charcmd.Get(), "r");
00542   if(!pipe) 
00543     {
00544        return 0; // Error
00545     }
00546    
00547   char buffer[128];    
00548   while(!feof(pipe)) 
00549     {
00550       if(fgets(buffer, 128, pipe) != NULL)
00551         {
00552           result += buffer;
00553         }
00554     }
00555   
00556   pclose(pipe);
00557 
00558   // Extract the CPU usage percentage from the result
00559   double cpupercentage = 0.0f;
00560   result.UnFormat(__L("%%CPU\n %lf\n"), &cpupercentage);
00561 
00562   return static_cast<int>(cpupercentage);
00563 }
