00708 bool XLINUXSYSTEM::ShutDown(XSYSTEM_CHANGESTATUSTYPE type)
00709 {
00710   int       opt_reboot = 0;
00711   int     fd;
00712 
00713   switch(type)
00714     {
00715       case XSYSTEM_CHANGESTATUSTYPE_REBOOT          : opt_reboot = 1;   break;     
00716       case XSYSTEM_CHANGESTATUSTYPE_POWEROFF        : opt_reboot = 0;   break;
00717                                         default     : break;
00718     }
00719 
00720   setpriority(PRIO_PROCESS, 0, PRIO_MIN);
00721                                                         
00722   whom = getlogin();
00723 
00724   setpriority(PRIO_PROCESS, 0, PRIO_MIN);
00725     signal(SIGINT,  int_handler);
00726     signal(SIGHUP,  int_handler);
00727     signal(SIGQUIT, int_handler);
00728     signal(SIGTERM, int_handler);
00729 
00730     chdir("/");
00731 
00732     signal(SIGPIPE, SIG_IGN);   
00733     signal(SIGINT,  SIG_IGN);   // now there's no turning back... 
00734 
00735     kill(1, SIGTSTP);             // tell init not to spawn more getty's 
00736         
00737     sync();
00738 
00739     signal(SIGTERM, SIG_IGN);
00740 
00741     if(fork() > 0) sleep(1000); // the parent will die soon... 
00742 
00743     setpgrp();                    // so the shell wont kill us in the fall 
00744     
00745   kill(-1, SIGTERM);          // a gentle kill of all other processes except init 
00746     sleep(2);
00747     
00748     kill(-1, SIGKILL);          // now use brute force... 
00749 
00750     acct(NULL);                 // turn off accounting 
00751 
00752     sync();
00753     sleep(2);
00754 
00755     swap_off();                 // remove swap files and partitions using swapoff 
00756 
00757     unmount_disks();              // unmount disks... 
00758 
00759     sync();
00760     sleep(1);
00761 
00762   if(opt_reboot) 
00763     {
00764           reboot(LINUX_REBOOT_CMD_RESTART);
00765       } 
00766    else 
00767     {
00768           reboot(LINUX_REBOOT_CMD_POWER_OFF);
00769     }         
00770 
00771   exit(0); // to quiet gcc 
00772 
00773   return true;
00774 }
