00888 void swap_off()
00889 {
00891     //  unmounted. It you don't have swapoff(1) or use mount to
00892     //  add swapspace, this may not be necessary, but I guess it
00893     //  won't hurt 
00894 
00895     int   pid;
00896     int   result;
00897     int   status;
00898 
00899     sync();
00900 
00901     if((pid = fork()) < 0) 
00902     {
00903           // Cannot fork for swapoff. Shrug!
00904           return;
00905       }
00906 
00907     if(!pid) 
00908     {
00909           execl("/sbin/swapoff" , SWAPOFF_ARGS, NULL);
00910           execl("/etc/swapoff"  , SWAPOFF_ARGS, NULL);
00911           execl("/bin/swapoff"  , SWAPOFF_ARGS, NULL);
00912 
00913           execlp("swapoff"      , SWAPOFF_ARGS, NULL);
00914 
00915           // Cannot exec swapoff, hoping umount will do the trick.
00916           exit(0);
00917       }
00918 
00919     while((result = wait(&status)) != -1 && result != pid);
00920 }
