00932 void unmount_disks()
00933 {
00934     // better to use umount directly because it may be smarter than us 
00935 
00936     int   pid;
00937     int   result;
00938     int   status;
00939 
00940     sync();
00941 
00942     if((pid = fork()) < 0) 
00943     {
00944           // Cannot fork for umount, trying manually.
00945           unmount_disks_ourselves();
00946           return;
00947       }
00948 
00949     if(!pid) 
00950     {
00951           execl(_PATH_UMOUNT, UMOUNT_ARGS, NULL);
00952 
00953         // Cannot exec %s, trying umount. _PATH_UMOUNT
00954 
00955         execlp("umount", UMOUNT_ARGS, NULL);
00956 
00957         // Cannot exec umount, giving up on umount.
00958           exit(0);
00959       }
00960 
00961     while ((result = wait(&status)) != -1 && result != pid);
00962 
00963     // Unmounting any remaining filesystems...
00964 
00965     unmount_disks_ourselves();
00966 }
00967 
00968 
00978 void unmount_disks_ourselves()
00979 {
00980     // unmount all disks 
00981 
00982     FILE*           mtab;
00983     struct mntent*  mnt;
00984     char*           mntlist[128];
00985     int             i;
00986     int             n;
00987     char*           filesys;
00988     
00989     sync();
00990 
00991     if(!(mtab = setmntent(_PATH_MTAB, "r"))) 
00992     {
00993           // shutdown: Cannot open %s _PATH_MTAB;
00994           return;
00995       }
00996 
00997     n = 0;
00998     while (n < 100 && (mnt = getmntent(mtab))) 
00999     {
01000           mntlist[n++] = strdup(mnt->mnt_fsname[0] == '/' ?
01001             mnt->mnt_fsname : mnt->mnt_dir);
01002       }
01003 
01004     endmntent(mtab);
01005 
01006     // we are careful to do this in reverse order of the mtab file 
01007 
01008     for(i = n - 1; i >= 0; i--) 
01009     {
01010           filesys = mntlist[i];
01011 
01012           if(umount(mntlist[i]) < 0)
01013         {
01014                 // shutdown: Couldn't umount %s filesys
01015         }
01016       }
01017 }
