01065       XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_register: error"));
01066       snd_mixer_close(handle);
01067       return false;
01068     }
01069 
01070   int ret = snd_mixer_load(handle);
01071   if(ret < 0) 
01072     {
01073       XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_load: error"));
01074       snd_mixer_close(handle);
01075       return false;
01076     }
01077 
01078   snd_mixer_selem_id_alloca(&sid);
01079 
01080   //sets simple-mixer index and name
01081   snd_mixer_selem_id_set_index(sid  , mix_index);
01082   snd_mixer_selem_id_set_name(sid   , mix_name1);
01083 
01084   elem = snd_mixer_find_selem(handle, sid);
01085   if(!elem) 
01086     {
01087       snd_mixer_selem_id_set_index(sid  , mix_index);
01088       snd_mixer_selem_id_set_name(sid   , mix_name2);
01089       
01090       elem = snd_mixer_find_selem(handle, sid);
01091       if(!elem) 
01092         {
01093           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_find_selem: error"));
01094 
01095           snd_mixer_close(handle);
01096           return false;   
01097         }
01098     }
01099 
01100   long minv, maxv;
01101 
01102   snd_mixer_selem_get_playback_volume_range(elem, &minv, &maxv);
01103   
01104   XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Alsa volume] Volume range <%i,%i>"), minv, maxv);
01105 
01106   if(read) 
01107     {
01108       if(snd_mixer_selem_get_playback_volume(elem, (snd_mixer_selem_channel_id_t)0, level) < 0) 
01109         {
01110           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_get_playback_volume: error"));  
01111           snd_mixer_close(handle);
01112           return false;
01113         }
01114 
01115       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Alsa volume] Get volume %i with status %i"), *level, ret);
01116 
01117       // make the value bound to 100 
01118       *level  -= minv;
01119       maxv    -= minv;
01120       minv     = 0;
01121      
01122       *level = 100 * (*level) / maxv; // make the value bound from 0 to 100
01123     }    
01124    else   
01125     {
01126       if(*level < 0 || *level > 100) // out of bounds
01127         {
01128           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] out of bounds: error"));
01129           return false;
01130         }
01131 
01132       //minv = 0;
01133       //maxv = 100;
01134 
01135      
01136       *level = (*level * (maxv - minv) / (100-1)) + minv;
01137       
01138       if(snd_mixer_selem_set_playback_volume_range(elem, minv, maxv) < 0)
01139         {
01140           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_set_playback_volume_range: error"));
01141 
01142           snd_mixer_close(handle);
01143           return false;
01144         }
01145          
01146       if(snd_mixer_selem_set_playback_volume_all(elem, *level) < 0) 
01147         {
01148           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_set_playback_volume_all: error"));
01149 
01150           snd_mixer_close(handle);
01151           return false;
01152         }
01153    
01154       /*
01155       if(snd_mixer_selem_set_playback_volume(elem, (snd_mixer_selem_channel_id_t)0, *level) < 0) 
