01171 
01172       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Alsa volume] Set volume %i with status %i"), *level, ret);        
01173     }
01174 
01175   snd_mixer_close(handle);
01176 
01177   return true;
01178 
01179   #else
